var gulp = require('gulp')
var autoImports = require('../../../index')

var dest = 'tests/output/preset-outputs'


gulp.task('preset:es5', function () {
	return gulp
		.src([
			'./tests/input/test/es5/**/*',
			'./tests/other-test-folder/es5/**/*',
		])
		.pipe(
			autoImports({
				preset: 'es5',
				dest: dest,
				fileName: 'preset-es5.js',
			}),
		)
		.pipe(gulp.dest(dest))
})


